import React, {useState} from 'react';
import Distribution from '../components/Distribution';
import NumberInput from '../components/Numbernput';


function Uniforme() {
    const [a, setA] = useState(0);
    const [b, setB] = useState(1);
    console.log(b);

    return(
        <div>
        <Distribution name={"Uniforme Continua"} funcion={()=>1/(b-a)} />
        <NumberInput max={b-1} handleChange={(value)=> setA(value)} />
        <NumberInput min={a+1} handleChange={(value)=> setB(value)} />

        </div>

    )
}

export default Uniforme
