import Graph from './Graph';

class Distribution extends Component{

  evaluateFunction(f, min, max, step){
    let data = [];
    let labels = [];

    for(var i=min; i<max; i+=step){
        labels.push(i);
        data.push(f(i));
    }

    return [labels, data];
  }

  constructor () {
    super();
  }

  render(){
    d = this.evaluateFunction(()=>{x}, 2,4, 0.5);
