import React, {useState} from 'react';
import Distribution from '../components/Distribution';
import NumberInput from '../components/Numbernput';


function Uniforme() {
    const [a, setA] = useState(0);
    const [b, setB] = useState(0);

    return(
        <div>
        <Distribution name={"Uniforme Continua"} funcion={()=>1/(b-a)} />
        <NumberInput text={"a"} handleChange={(e)=> setA(e.target.value)} />
        <NumberInput text={"b"} handleChange={(e)=> setB(e.target.value)} />
        console.console.log(a);
        </div>

    )
}

export default Uniforme
