import React, {useState} from 'react';
import Distribution from '../components/Distribution';
import NumberInput from '../components/Numbernput';


function Uniforme() {
    const [a, setA] = useState(0);
    const [b, setB] = useState(1);

    console.log("a:"+a + ", b:"+b);
    console.log("minB:"+ (a+1) + ", maxA:"+(b-1));
    return(
        <div>
        <span><Distribution name={"Uniforme Continua"} funcion={()=>1/(b-a)} /></span>

        <span><NumberInput text={"a"} default={0} handleChange={(e)=> setA(e.target.value)} />
        <NumberInput text={"b"} default={1} handleChange={(e)=> setB(e.target.value)} />
        </span>
        </div>

    )
}

export default Uniforme
