import React from 'react';
import { Line } from 'react-chartjs-2';

const Graph = (props) => {
    console.log("aa: " + props.labels.indexOf(props.max) + ", bb:" + props.labels.length);
    const graphData= {
        labels: props.labelsSimple,
        datasets: [
          {
            backgroundColor: 'blue',
            highlightStroke: "rgba(220,220,220,1)",
            borderWidth: 0,
            data: [...props.data].splice(0, props.labels.indexOf(props.min)),
            pointBackgroundColor: 'rgba(0,0,0,0)',
            pointBorderColor: 'rgba(0,0,0,0)'
          },
          {
            backgroundColor: 'red',
            highlightStroke: "rgba(220,220,220,1)",
            borderWidth: 0,
            data: [...props.data].splice(props.labels.indexOf(props.min), props.labels.indexOf(props.max)),
            pointBackgroundColor: 'rgba(0,0,0,0)',
            pointBorderColor: 'rgba(0,0,0,0)'
          },
          {
            backgroundColor: 'yellow',
            highlightStroke: "rgba(0,220,220,1)",
            borderWidth: 0,
            data: [...props.data].splice(props.labels.indexOf(props.max), props.labels.length-1),
            pointBackgroundColor: 'rgba(0,0,0,0)',
            pointBorderColor: 'rgba(0,0,0,0)'
          }

        ]
    }

    return (
        <span><div style={{ width: "500px" }}>
            <Line
                data={graphData}
                options={{
                    legend:{
                        display: false,
                    },
                   responsive: true,
                   title: {text: "Función de Densidad", display: true},
                   scales:{
                       yAxes:[
                           {
                               ticks:{
                                    beginAtZero: true,
                               }
                           }
                       ],
                       xAxes:[
                        {
                            ticks:{
                                autoSkip: false
                            },
                            gridLines:{
                                display: false
                            }
                        }
                    ]
                   }
                }}
            />
        </div></span>
    );
}

export default Graph
