import React from 'react';
import { Bar } from 'react-chartjs-2';

export default class Graph extends React.Component {

    constructor(){
        super();
        this.state={
            data: [],
            labels: []
        }
    }

    getPoints = (f, nPoints, min, max) => {
        let data = [];
        let labels =[];

        for(var i=min; i<max; i+=(max-min)/nPoints){
            labels.push(i);
            data.push(f(i));
        }

        this.setState(data= data, labels= labels);
    }

    render() {
        this.getPoints();

        return (
            <span><div style={{ width: "500px" }}>
                <Bar
                    data={this.state.data}
                    options={{
                        legend:{
                            display: false
                        },
                       responsive: true,
                       title: {text: 'FUNCIÓN DISTRIBUCIÓN', display: true},
                       scales:{
                           yAxes:[
                               {
                                   ticks:{
                                        autoSkip: true,
                                        beginAtZero: true,
                                        min: 0,
                                   }
                               }
                           ]
                       }
                    }}
                />
            </div></span>
        );
    }
}
