import React from 'react';
import Distribution from '../components/Distribution';
import NumberInput from '../components/Numbernput';


const Uniforme = () => {
    let a=2;
    let b=0;

    return(
        <div>
        <Distribution name={"Uniforme Continua"} funcion={()=>(a*b)/2} />
        <NumberInput text={"a"} handleChange={(e)=> a=e.target.value} />
        <NumberInput text={"b"} handleChange={(e)=> b=e.target.value} />
        console.console.log(a);
        </div>

    )
}

export default Uniforme
